mapper.select:
select chat_announce.id, room_id, content, image_url, is_pinned, create_date, sys_user.username from chat_announce join sys_user on chat_announce.user_id = sys_user.id where room_id = #{roomId}
AND create_date < #{lastCreateDate}
order by is_pinned,create_date desc LIMIT #{limit}

select chat_announce.id, room_id, content, image_url, is_pinned, create_date, sys_user.username from chat_announce join sys_user on chat_announce.user_id = sys_user.id where room_id = #{roomId} and user_id = #{userId}
AND create_date < #{lastCreateDate}
order by create_date desc LIMIT #{limit}
